import { ApplicationsProps } from "./types";
import React, { useEffect, useState } from 'react';

export default function ApplicationLinks(props: ApplicationsProps) {
    const { application, appVal, appType } = props;
    const [alfredCount, setAlfredCount] = useState(-1);
    
    useEffect(() => {
        fetch(`http://${location.host}/api/v1/proxy/alfred/user_id/${ appVal }`)
        .then(res => res.json())
        .then((response : { payload : {data: any[]} }) => {
            console.log(response)
            setAlfredCount(response.payload.data.length);
        })
        .catch(e =>
            {
                console.log(e);
                setAlfredCount(0)
            }
        );
    }, []);

    let url: string = "";
    let infoType: string = "";
    if (application === "ALFRED") {
        if (appType === "USER_ID") {
            url = "https://alfred-tst.u.chimera.azure.cyber.gc.ca/?expression=MATCH%20(email:EMAIL_ADDRESS)%20WHERE%20email.value%20in%20[%22" + appVal + "%22]%20return%20email.value,%20email.maliciousness,%20email.uri"
            infoType = "user id";
        } else {
            url = "https://alfred-tst.u.chimera.azure.cyber.gc.ca/?expression=MATCH%20(ip%3AIP_ADDRESS)%20WHERE%20ip.value%20IN%20%5B%22" + appVal + "%22%5D%20RETURN%20ip.value%2C%20ip.maliciousness%2C%20ip.creation_date%2C%20ip.created_by%2C%20ip.uri%2C%20ip.report_uri";
            infoType = "IP";
        }
    }

    return (
        <div style={{display: "flex", "border-bottom": "1px solid #F0F0F0", "padding-bottom": "1px"}}>
            <a href={ url } target="_blank"><img style={{float: "left"}} height="17" width="30" alt="Alfred" src="data:image/png;base64,"/></a>
            
            {alfredCount > 0 ? 
             <p style={{float: "left", "display": "table-cell", "line-height": "20px", height: 20 + "px"}}>
             Alfred has seen this {infoType} {alfredCount} time(s), search the <a href={ url } target="_blank">Alfred</a> knowledge base.
             </p>
             :
             <p style={{float: "left", "display": "table-cell", "line-height": "20px", height: 20 + "px"}}>
             Alfred has not seen this {infoType}, search the <a href={ url } target="_blank">Alfred</a> knowledge base.
             </p>
        
            }
           
        </div>
    );
}